import classes from "./AvailableMeals.module.css";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import { useState, useCallback, useEffect } from "react";

const fetchUrl =
  "https://react-fetch-movies-12638-default-rtdb.firebaseio.com/meals.json";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [error, setError] = useState("");
  const [isLoading, setIsLoading] = useState(false);

  const asyncMealsHandler = useCallback(async () => {
    try {
      const response = await fetch(fetchUrl);

      setIsLoading(true);
      setError(false);
      if (!response.ok) {
        throw new Error("Fetch unsuccessful!");
      }

      const data = await response.json();
      const loadedMeals = [];

      for (const key of Object.keys(data)) {
        loadedMeals.push({
          id: key,
          name: data[key].name,
          description: data[key].description,
          price: +data[key].price,
        });
      }
      setMeals(loadedMeals);
    } catch (error) {
      setError(error.message);
    }
    setIsLoading(false);
  }, []);

  useEffect(() => {
    asyncMealsHandler();
  }, [asyncMealsHandler]);

  const mealsList = meals.map(
    (meal) => console.log(meal)
    // <MealItem
    //   key={meal.id}
    //   id={meal.id}
    //   name={meal.name}
    //   description={meal.description}
    //   price={meal.price}
    // />
  );

  return (
    <section className={classes["available-meals"]}>
      <Card>{/* <ul>{mealsList}</ul> */}</Card>
      {error && <p>{error}</p>}
    </section>
  );
};

export default AvailableMeals;
